import requests
import json
from bs4 import BeautifulSoup

URL = "https://petset.com/pet-names/popular-fish-names/"

r = requests.get(URL)

data = r.text

soup = BeautifulSoup(data, 'html.parser')

names = soup.find('div', { 'class': 'girl-names' }).findAll('li')

list = []

for name in names:
  list.append(name.get_text().lower().capitalize())
  with open('female-fish.json', 'w') as outfile:
    json.dump(list, outfile)
